import sys
import json

is_linux = sys.platform == 'linux'
is_android = sys.platform == 'linux4'

if is_linux:
	from urllib import request
if is_android:
	import urllib
  
def modexp(base, exponent, modulus):
	if modulus == 1:
		return 0
	# Assert :: (modulus - 1) * (modulus - 1) does not overflow base
	result = 1
	base = base % modulus
	while exponent > 0:
		if ( exponent % 2 == 1):
			result = (result * base) % modulus
		exponent = exponent >> 1
		base = (base * base) % modulus
	return result
	
def mapGq2Zq(element):
	if element < enc_order:
		return element - 1
	return enc_modulus - element - 1
	
def urlopen(url):
	print('urlopen: ' + url)
	if is_android:
		return urllib.urlopen(url)
	if is_linux:
		return request.urlopen(url)
	return None


def publicSignatureKey(secretKey):	
	return modexp(sig_generator, secretKey, sig_modulus)

def getBallots(board):
    ballotsJson = urlopen(board + '/ballots')
    ballots = json.load(ballotsJson)
    return ballots['messages'] # verify signature ..
    
def getBallot(board, publicSignatureKey):
	ballots = getBallots(board)
	for ballot in ballots:
		if ballot['publicSignatureKey'] == str(publicSignatureKey):
			return ballot
	return Null
	
def getOptions(board):	
	electionDetailJson = urlopen(board + '/electionDetail')	
	electionDetail = json.load(electionDetailJson)	
	message = electionDetail['message']	
	issuesJson = message['issues']	
	issues = json.loads(issuesJson)
	options = []
	for issue in issues:
		addOptionsFromIssue(options, issue)
	return options
	
def addOptionsFromIssue(options, issue):
	for option in issue['options']:
		options.append(option)
		for innerIssue in option['issues']:
			addOptionsFromIssue(options, innerIssue)
	
def getPartialEncryptionKeys(board):	
	partialEncryptionKeysJson = urlopen(board + '/partialEncryptionKeys')
	partialEncryptionKeys = json.load(partialEncryptionKeysJson)
	return partialEncryptionKeys['messages'] # verify signature
	
def getEncryptionKey(board):
	partialEncryptionKeys = getPartialEncryptionKeys(board)
	result = 1
	for partialKey in partialEncryptionKeys:
		result = (result * int(partialKey['partialKey'])) % enc_modulus
	print("use encryptionKey: {}".format(result))
	return result
    
def verify(sid, eid, x, r):
	print("verify: {}, {}, {}, {}".format(sid, eid, x, r))
	board = sid + '/board/' + eid
	encryptionKey = getEncryptionKey(board)
	publicKey = publicSignatureKey(x)	
	ballot = getBallot(board, publicKey)	
	b = int(ballot['encryption']['b'])
	m2 = modexp(encryptionKey, enc_order-r, enc_modulus)
	m = (b * m2) % enc_modulus
	m = mapGq2Zq(m)
	print('decimal:', m)
	m = '{0:b}'.format(m)
	m = m[::-1] # reverse m
	print('start -->', m)
	options = getOptions(board)	
	selectedOptions = [];
	for key, value in enumerate(m):		
		if value == '1':			
			option = options[key]			
			selectedOptions.append(option)	
	return selectedOptions

def parseAndVerify(msg):
	tokens = msg.split('=')
	s = tokens[1][:-1]
	e = tokens[2][:-1] # cut of x of x=
	x = int(tokens[3][:-1]) # cut of n of n=
	r = int(tokens[4])
	return verify(s,e,x,r)


if __name__ == '__main__':
	if not len(sys.argv) == 2 or len(sys.argv) == 4:
		print('''usage:
	python verifier.py <electionid> <secretkey> <randomization>
		or
	python verifier.py <fullmessage> # fullmessage: e=<electionid>x=<secretkey>r=<randomization>
	''')
	
	if len(sys.argv) == 2:
		parseAndVerify(sys.argv[1])
	if len(sys.argv) == 4:
		e = sys.argv[1]
		x = int(sys.argv[2])
		r = int(sys.argv[3])	
		verify(e, x, r)
	
	


